import React from 'react';
import Api from "../utils/Api";
import { MDBDataTable} from 'mdbreact';

const DatatablePage = () => {

  let data = {
    columns: [
      {
        label: 'Image',
        field: 'image',
        sort: 'asc',
        width: 150
      },
      {
        label: 'Name',
        field: 'name',
        sort: 'asc',
        width: 270
      },
      {
        label: 'Phone',
        field: 'phone',
        sort: 'asc',
        width: 200
      },
      {
        label: 'Email',
        field: 'email',
        sort: 'asc',
        width: 100
      },
      {
        label: 'DOB',
        field: 'DOB',
        sort: 'asc',
        width: 150
      }
    ],
    rows: [{name: "Krin", email: "krin.steward@example.com", image: "https://randomuser.me/api/portraits/women/34.jpg", phone: "(877)-580-1684", DOB: "1964-07-02T01:13:47.389Z"}
          ,{name: "Kent", email: "kent.ferguson@example.com", image: "https://randomuser.me/api/portraits/men/89.jpg", phone: "(805)-018-4976", DOB: "1992-10-25T11:12:19.284Z"}]

  };

  // Api.getusers().then(rows=>{this.setState({ rows });});

  fetch("https://randomuser.me/api/?results=50&nat=us", {
      method: "GET",
    })
      .then(res => res.json())
      .then(json => {
        let rows = [];
        json.data.results.forEach(item => rows.push({
          name: item.name.first,
          email: item.email,
          image: item.picture.large,
          phone: item.phone,
          DOB: item.dob.date,
        }));

        this.setState({ rows });
      })
      .catch(err => console.error(err));

  console.log(data);
  return (
    <MDBDataTable
      striped
      bordered
      data={data}
    />
  );
  // return Api.getusers().then(results=>{


  // });



}

export default DatatablePage;
