import React from "react";
import Api from "../utils/Api";
import Records from "./Record"
class Datastore extends React.Component {
    state = {
employees:[]
    }
    componentDidMount =()=>{
        let userdata = []
        Api.getusers().then(results=>{
            // console.log (results.data.results)
            for(i=0; i<results.data.results; i++){
                let employeedata= {
                    name: results.data.results[i].name.first,
                    email: results.data.results[i].email,
                    location: results.data.results[i].location,
                    gender: results.data.results[i].gender
                }
                console.log (employeedata)
                userdata.push(employeedata)
            }
            this.setState({
                employees:employeedata
            })
        })
    }
    render(){
        var rec = this.state.employees
        return(
<div>
<h1>
    Hello world
</h1>

   {rec.map((data, i) => ( 

        <Records
        name = {data.name}
        email = {data.email}
        location = {data.location}
        gender = {data.gender}
        key={i}
        />

        ))}

</div>
        )
    }
}
export default Datastore;
