import React from 'react';
import Api from "../../utils/Api.js";
import Card from 'react-bootstrap/Card';
import ListGroup  from 'react-bootstrap/ListGroup';
import ListGroupItem from 'react-bootstrap/ListGroupItem';
import { Component } from 'react';
import { Col, Form, Button, Container, Row, Jumbotron, CardColumns } from 'react-bootstrap';


class DatatablePage extends React.Component {

  state = {
    data: [{"id": 1, "name": "Frank Sutton", "email": "frank.sutton@example.com", "dob": '1979-01-28', "notes": "No Problem", "picture": "https://randomuser.me/api/portraits/med/men/74.jpg"}
          ,{"id": 2, "name": "Philip Prescott", "email": "philip.prescott@example.com", "dob": '1983-01-02', "notes": "No Problem", "picture": "https://randomuser.me/api/portraits/med/men/34.jpg"}]

  };

  componentDidMount =()=>{

    Api.getPatient(localStorage.getItem('searchID')).then(json => {
        let data = [];
        json.forEach(item => data.push({
          name: item.name,
          id: item.id,
          email: item.email,
          notes: item.notes,
          dob: item.dob,
          picture: [<img src={item.picture} className="img-fluid" class="rounded-circle" alt=""/>]
        }));
        this.setState({ data });
        console.log(data );
      })
      .catch(err => console.error(err));

  }

  render() {
    return (
      <div>
      {this.state.data.map((data_,index)=> (
        <Card className="text-center">
          <Card.Header>Patient</Card.Header>
          <Card.Body>
            <Card.Img variant="top" src={data_[0].picture} />
            <Card.Title>{data_.name}</Card.Title>
            <Card.Text>
              Contact: {data_.email}<br/>
              DOB: {data_.dob}
            </Card.Text>
            <Button variant="primary">Back to all patients</Button>
          </Card.Body>
          <Card.Footer className="text-muted">2 days ago</Card.Footer>
        </Card>
      ))}
      </div>
    );

  }

};

export default DatatablePage;
