import React from 'react';
import Api from "../utils/Api";
import { MDBDataTable} from 'mdbreact';



class DatatablePage extends React.Component {

  state = {
    columns: [
      {
        label: 'Image',
        field: 'image',
        sort: 'asc',
        width: 150
      },
      {
        label: 'Name',
        field: 'name',
        sort: 'asc',
        width: 270
      },
      {
        label: 'Phone',
        field: 'phone',
        sort: 'asc',
        width: 200
      },
      {
        label: 'Email',
        field: 'email',
        sort: 'asc',
        width: 100
      },
      {
        label: 'DOB',
        field: 'DOB',
        sort: 'asc',
        width: 150
      }
    ],
    rows: Api.getusers().then(results=>{return})

  };


  getEmployees = () => {
    fetch("https://randomuser.me/api/?results=50&nat=us", {
      method: "GET",
    })
      .then(res => res.json())
      .then(json => {
        let rows = [];
        json.results.forEach(item => rows.push({
          name: `${item.name.first} ${item.name.last}`,
          email: item.email,
          image: item.picture.large,
          phone: item.phone,
          DOB: item.dob.date,
        }));
        console.log(rows);
        this.setState({ rows });
      })
      .catch(err => console.error(err));
  }


  render() {
    return (
      <MDBDataTable
          striped
          bordered
          hover
          data={{ columns: this.state.columns, rows: this.state.rows }}
        />
    );

  }

};


